export default {
  email: (errors, value) => {
    if (!value) {
      errors.email = "Required";
    } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(value)) {
      errors.email = "Invalid email address";
    }
  }
};
