import React from "react";
import PropTypes from "prop-types";
import { Empty, Spin, Alert } from "antd";

import { Message } from "../";

const Messages = ({ isLoading, items }) => {
  return (
    <div className="messages">
      {isLoading && !items ? (
        <Spin tip="Loading...">
          <Alert
            message="Alert message titile"
            descriptio="Loading"
            type="info"
          />
        </Spin>
      ) : items ? (
        items.map(item => <Message key={item._id} {...item} />)
      ) : (
        <Empty
          image={Empty.PRESENTED_IMAGE_SIMPLE}
          description="Choose dialog"
        />
      )}
    </div>
  );

  return items ? (
    <div>
      {items.map(item => (
        <Message key={item._id} {...item} />
      ))}
    </div>
  ) : (
    <Empty image={Empty.PRESENTED_IMAGE_SIMPLE} description="Choose dialog" />
  );
};

Messages.propTypes = {
  items: PropTypes.array
};

export default Messages;
