import React from "react";
import classNames from "classnames";
import format from "date-fns/format";
import isToday from "date-fns/isToday";

import "./DialogItem.scss";

import { IconReaded } from "../";

const getMessageTime = created_at => {
  if (isToday(created_at)) {
    return format(created_at, "HH:mm");
  } else {
    return format(created_at, "MM.dd.yyyy");
  }
};

const getAvatar = avatar => {
  if (avatar) {
    return (
      <img
        src="https://live.staticflickr.com/2693/4111117620_2945c576c4_z.jpg"
        alt=""
      />
    );
  } else {
    //make
  }
};

const DialogItem = ({ user, message, unreaded, isMe }) => (
  <div
    className={classNames("dialogs__item", {
      "dialogs__item--online": user.isOnline
    })}
  >
    <div className="dialogs__item-avatar">
      {getAvatar(
        "https://live.staticflickr.com/2693/4111117620_2945c576c4_z.jpg"
      )}
      {/* <img src={user.avatar} alt={`${user.fullname} avatar`} /> */}
    </div>
    <div className="dialogs__item-info">
      <div className="dialogs__item-info-top">
        <b>{user.fullname}</b>
        <span>
          {getMessageTime(message.created_at)}
          {/* <Time date={getMessageTime} /> */}
          {/* 13:03 */}
        </span>
      </div>
      <div className="dialogs__item-info-bottom">
        <p>{message.text}</p>
        <IconReaded isMe={true} isReaded={false} />
        {unreaded > 0 && (
          <div className="dialogs__item-info-bottom-count">
            {unreaded > 9 && "+9"}
          </div>
        )}
      </div>
    </div>
  </div>
);

export default DialogItem;
