import "core-js/modules/es.array.find";
import React from 'react';
import copy from 'clipboard-copy';
import { shallow, mount } from 'enzyme';
import { PathlineRenderer, styles } from './PathlineRenderer';
import classes from '../../../../test/classes';
jest.mock('clipboard-copy');
var pathline = 'foo/bar';
var props = {
  classes: classes(styles)
};
it('renderer should a path line', function () {
  var actual = shallow(React.createElement(PathlineRenderer, props, pathline));
  expect(actual).toMatchSnapshot();
});
test('should copy text on click', function () {
  var actual = mount(React.createElement(PathlineRenderer, props, pathline));
  actual.find('button').simulate('click');
  expect(copy).toBeCalledWith(pathline);
});