import "core-js/modules/es.function.name";
import deepfreeze from 'deep-freeze-es6';
import processSections from '../processSections';
var sections = deepfreeze([{
  sections: [{
    name: 'Components',
    components: [{
      props: {
        displayName: 'Button'
      },
      module: 1
    }]
  }]
}]);
describe('processSections', function () {
  it('should recursively process all sections and components', function () {
    var result = processSections(sections);
    var sectionsExpected = result[0].sections || [];
    expect(sectionsExpected.length && sectionsExpected[0].components && sectionsExpected[0].components[0].name).toBe('Button');
  });
  it('should set visibleName property on each section', function () {
    var result = processSections(sections);
    var sectionsExpected = result[0].sections || [];
    expect(sectionsExpected[0].visibleName).toBe('Components');
  });
});